import './style.css'
import playIcon from '../../assets/play.svg'
import pauseIcon from '../../assets/pause.svg'
import previousIcon from '../../assets/previous.svg'
import nextIcon from '../../assets/next.svg'

export default function Controls({
    audioRef, 
    currentMusic, 
    iconBtn, 
    setIconBtn,
    handleChandeMusic
}) {

    function playPause() {

        if (audioRef.current.paused) {
            audioRef.current.play();
            setIconBtn('pause');
            return;
        }

        audioRef.current.pause();
        setIconBtn('play');
    }

    return(
        <div className='container-controls'>
            <div className='preview-names'>
                <h2>{currentMusic.title}</h2>
                <strong>{currentMusic.artist}</strong>
            </div>

            <div className='container-player'>    
                <div className='container-buttons'>
                    <img 
                        src={stopIcon} 
                        alt='Stop' 
                        className='btn-controls' 
                        onClick={() => stop()}    
                    />
                    <img 
                        src={previousIcon} 
                        alt='Previous' 
                        className='btn-controls'
                        onClick={() => handleChandeMusic('previous')}

                    />
                    <img 
                        src={iconBtn === 'pause' ? pauseIcon : playIcon} 
                        alt='Pause' 
                        className='btn-play-pause'
                        onClick={() => playPause()}
                    />
                    <img 
                        src={nextIcon} 
                        alt='Next' 
                        className='btn-controls'
                        onClick={() => handleChandeMusic('next')}

                    />
                </div>
                <div className='container-progress'>
                    <strong className='start'>0</strong>
                    <div className='contrainer'>
                        <div className='progress-line'></div>
                        <div className='progress-line-color'></div>
                    </div>
                    <strong className='end'>3:38</strong>
                </div>

            </div>

            <div className='empty'>

            </div>
        </div>

    )

}
