import { useRef } from 'react';
import nextIcon from '../../assets/next.svg';
import pauseIcon from '../../assets/pause.svg';
import playIcon from '../../assets/play.svg';
import previousIcon from '../../assets/previous.svg';
import stopIcon from '../../assets/stop.svg';
import './style.css';

export default function Controls({
    audioRef, 
    currentMusic, 
    iconBtn, 
    setIconBtn,
    handleChangeMusic
}) {

    let intervalProgress = null

    const progressRef = useRef(null);

    function playPause() {

        intervalProgress = setInterval(() => {

            if (audioRef.current.paused) {
                clearInterval(intervalProgress);
            }

            const duration = audioRef.current.duration / 60;
            const currentProgress = ((audioRef.current.durationTime / 60) * 100) / duration;

            progressRef.current.style.width = `${currentProgress}%`;

        }, 1000);

        if (audioRef.current.paused) {
            audioRef.current.play();
            setIconBtn('pause');
            return;
        }

        audioRef.current.pause();
        setIconBtn('play');
    }

    function stop() {
        setIconBtn('play');
        audioRef.current.pause();
        audioRef.current.currentTime = 0;        
    }

    return(
        <div className='container-controls'>
            <div className='preview-names'>
                <h2>{currentMusic.title}</h2>
                <strong>{currentMusic.artist}</strong>
            </div>

            <div className='container-player'>    
                <div className='container-buttons'>
                    <img 
                        src={stopIcon} 
                        alt='Stop' 
                        className='btn-control' 
                        onClick={() => stop()}    
                    />
                    <img 
                        src={previousIcon} 
                        alt='Previous' 
                        className='btn-control'
                        onClick={() => handleChangeMusic('previous')}

                    />
                    <img 
                        src={iconBtn === 'pause' ? pauseIcon : playIcon} 
                        alt='Pause' 
                        className='btn-play-pause'
                        onClick={() => playPause()}
                    />
                    <img 
                        src={nextIcon} 
                        alt='Next' 
                        className='btn-control'
                        onClick={() => handleChangeMusic('next')}

                    />
                </div>
                <div className='container-progress'>
                    <strong className='start'>0</strong>
                    <div className='container-line'>
                        <div className='progress-line'></div>
                        <div 
                            className='progress-line-color'
                            ref={progressRef}
                        >

                        </div>
                    </div>
                    <strong className='end'>3:38</strong>
                </div>

            </div>

            <div className='empty'>

            </div>
        </div>

    )

}
